import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { useHistory } from 'react-router-dom'
import { authActions } from '../../../../redux/store/Auth'
import FormButton from '../../../atoms/button/Button'
import style from './Login.css'

const Div = style()

const Login = () => {
    const isLogedIn = useSelector((state: any) => state.auth.isLogedIn)
    const dispatch = useDispatch()
    const history = useHistory()
    const [ username, setUsername ] = useState()
    const [ password, setPassword ] = useState()
    // console.log(isLogedIn)
    const handleSubmit = async (e: any) => {
        e.preventDefault()
        //dispatch(authActions.login())
        const data = {
            username,
            password
        }
        const response = await fetch('http://127.0.0.1:5002/api/v1/auth/admin/signin', {
            method: 'POST',
            mode: 'cors',
            cache: 'no-cache',
            credentials: 'same-origin',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify(data),
            redirect: 'follow'
        }).then(res => {
            console.log(res)
        }).catch(err => {
            console.log(err)
        })
    }
  return (
    <Div>
        <div className='form-container'>
            <div className="left">
                <img src="https://cdni.iconscout.com/illustration/premium/thumb/admin-manage-dashboard-online-with-laptop-1886524-1597935.png" alt="bg_image" />
            </div>

            <div className="right">
                <form className="form" onSubmit={handleSubmit}>
                    <h2 className="form-header">ADMIN SIGNIN</h2>
                    <input type="text" placeholder="Username" value={username} onChange={(e: any) => setUsername(e.target.value)} />
                    <input type="password" placeholder="Password" value={password} onChange={(e: any) => setPassword(e.target.value)} />
                    <div className="btn-container">
                        <FormButton
                            label="Sign in"
                            background="#6f65e7"
                            width={540}
                            color="white"
                            padding={{
                                top: 15,
                                bottom: 15,
                                left: 20,
                                right: 20
                            }}
                            fontSize={15}
                            fontWeight={600}
                        />
                        {/* <div className="finger-ptint-btn">
                            <img src="https://www.fingertec.com/biometric_data_security/images/fingerprint_ani2.gif" alt="" />
                        </div> */}
                    </div>
                </form>
            </div>
        </div>
    </Div>
  )
}

export default Login
