import { legacy_createStore as createStore} from 'redux'

const counterReducer = (state: any = {counter: 0}, action: any) => {
    if (action.type === 'INC') return {counter: state.counter + 1}
    if (action.type === 'DEC') return {counter: state.counter - 1}
    if (action.type === 'ADD') return {counter: state.counter + action.payload}
    if (action.type === 'MINUS') return {counter: state.counter - action.payload}
    return state;
}

const randomMessageReducer = (state: any = {random: ''}, action: any) => {
    if (action.type === 'generateRandom') return {random: state.random + genRand(8)}
}

const store = createStore(counterReducer)

export default store;
