import styled from 'styled-components'

const style = (bgColor: string, width?: number, padding?: {top: number, right: number, bottom: number, left: number }) => styled.button`
    background: ${bgColor};
    width: ${width}px;
    border: none;
    padding: ${padding?.top ? padding?.top : 0}px ${padding?.right}px ${padding?.bottom}px ${padding?.left};
`

export default style
