import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { useHistory } from 'react-router-dom'
import FormButton from '../../../atoms/button/Button'
import style from '../../admin/login/Login.css'

const Div = style()

const Dashboard = () => {
    const [ admin, setAdmin ] = useState()
    const [ username, setUsername ] = useState()
    const [ password, setPassword ] = useState()
    const history = useHistory()

    useEffect(() => {
      axios({
        method: 'get',
        url: 'auth/admin/dashboard',
        headers: {
          token: localStorage.getItem('token') || ''
        }
      }).then(res => {
        if (res.data.loggedIn === true) {
          setAdmin(res.data.data.username)
        }
      }).catch (err => {
        if (err) {
          history.push('/')
        }
      })
    })

    const handleSubmit = (e: any) => {
      e.preventDefault()
      const data = {
          username,
          password
      }
      axios({
          method: 'POST',
          url: 'auth/admin/signin',
          data
      }).then(res => {
          localStorage.setItem('token', res.data.token)
          history.push('/admin-dashboard');
      }).catch(err => {
        console.log(err)
      })
    }
    return (
      <Div>
        <div className='form-container'>
          <div className="left">
              <img
                  src="https://www.oots.in/frontend/images/registration.png"
                  alt="bg_image"
              />
          </div>

          <div className="right">
              <form className="form" onSubmit={handleSubmit}>
                  <h2 className="form-header">ADMIN SIGNIN</h2>
                  <input type="text" placeholder="Username" onChange={(e: any) => setUsername(e.target.value)} />
                  <input type="password" placeholder="Password" onChange={(e: any) => setPassword(e.target.value)} />
                  <div className="btn-container">
                      <FormButton
                          label="Sign in"
                          background="#6f65e7"
                          width={540}
                          color="white"
                          padding={{
                              top: 15,
                              bottom: 15,
                              left: 20,
                              right: 20
                          }}
                          fontSize={15}
                          fontWeight={600}
                      />
                      {/* <div className="finger-ptint-btn">
                          <img src="https://www.fingertec.com/biometric_data_security/images/fingerprint_ani2.gif" alt="" />
                      </div> */}
                  </div>
              </form>
          </div>
      </div>
    </Div>
  )
}

export default Dashboard
