import styled from 'styled-components'

const style = (bgColor: string, width?: number, padding?: {to: number, right: number, bottom: number, left: number }) => styled.button`
    background: ${bgColor};
    width: ${width}px;
    border: none;
    padding: ${padding?.top} ${padding?.right} ${padding?.bottom} ${padding?.left};
`

export default style
