import React, { FC, useState } from 'react'
import style from './Button.css'

const [backgroundState, setBackgroundState] = useState(background)
const Button = style(backgroundState)

export interface IButtton {
    onClick?: Function,
    label?: string,
    background?: any,
    color?: string,
}

const FormButton:FC<IButtton> = ({onClick, label, background, color}) => {

  return <Button className={background && 'bg-color'}>{label}</Button>
}

export default FormButton
